mapper.update:
UPDATE lms_edu
member_idx = #{memberIdx}, edu_title = #{eduTitle}, edu_category = #{eduCategory}, category_idx = #{categoryIdx}, edu_type = #{eduType}, edu_keyword = #{eduKeyword}, edu_module = #{eduModule}, edu_state = #{eduState}, update_idx = #{updateIdx}, update_ip = #{updateIp}, update_date = CURRENT_TIMESTAMP
WHERE delete_yn = 'n' AND edu_idx = #{eduIdx}

UPDATE lms_edu
attachment_file_name = #{attachmentFileName}, attachment_file_origin = #{attachmentFileOrigin},
WHERE delete_yn = 'n' AND edu_idx = #{eduIdx}

UPDATE lms_edu
delete_idx = #{deleteIdx}, delete_ip = #{deleteIp}, delete_date = CURRENT_TIMESTAMP, delete_yn = 'y'
WHERE delete_yn = 'n' AND edu_idx = #{eduIdx}
